&lt;script&gt;
  var btn = document.querySelector('#J_btn');
  btn.addEventListener('click', function(){
    ap.getBluetoothAdapterState({
      success: function(res){
        if(!res.available) {
          ap.alert('抱歉，您的手机蓝牙暂不可用');
        }
        if(res.discovering) {
          ap.showToast('正在搜索蓝牙设备');
        }
      },
      fail: function(res) {
        ap.showToast(res.errorMessage);
      }
    });
  });
