  };
  var handlerPause = function(){
    ap.showToast('暂停播放')
  };
  var handlerStop = function(){
    ap.showToast('停止播放')
  };

  btnPlayBackgroundAudio.addEventListener('click', function(){
    ap.playBackgroundAudio({
      title: '舒缓轻音乐',
      url: 'https://os.alipayobjects.com/rmsportal/FOjtUAVjVsfldZgjBdxE.mp3',
